pragma solidity ^0.4.24;

import "../../openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "../../openzeppelin-solidity/contracts/token/ERC20/ERC20Detailed.sol";

contract CHEERToken is ERC20, ERC20Detailed {

  string private _name = 'CHEERToken';
  string private _symbol = 'CHEER';
  uint8 private _decimals = 0;
  address private _admin_address;
  uint private cheerCoolDownTime = 1 minutes;


  struct Message {
      address from;
      address to;
      string screenName;
      string message;
      bytes32 hashedId;
      uint timestamp;
  }

  struct ThanksMessage {
      address from;
      address to;
      string message;
      bytes32 hashedId;
      uint timestamp;
  }

  Message[] public cheerMessages;
  ThanksMessage[] public thanksMessages;
  mapping (address => uint[]) public sendMessages;

  mapping (address => uint[]) public recieveMessages;
  mapping (address => uint[]) public thanksSendMessages; 
  mapping (address => uint[]) public thanksRecieveMessages;

  mapping (bytes16 => uint[]) public tempMessages;

  mapping (address => string) public users;
  mapping (bytes16 => address) public userMap;

  mapping (bytes32 => uint) cheerHashedIdPureIdMap;
  mapping (bytes32 => uint) thanksHashedIdPureIdMap;

  function sendMessage(address _to, string memory _screenName, string memory _message, bytes32 _hashedId) public {
    address _from = msg.sender;
    _mint(_from,1);
    transfer(_to, 1);
    cheerMessages.push(Message(_from, _to,_screenName, _message, _hashedId, now));
    sendMessages[_from].push(cheerMessages.length-1);
    cheerHashedIdPureIdMap[_hashedId] = cheerMessages.length - 1;

    if (_to == 0x9876e3e8255D1D85d960CB5fA27F004e76cC3207) {
        tempMessages[s2b16(_screenName)].push(cheerMessages.length-1);
    } else {
        recieveMessages[_to].push(cheerMessages.length-1);
    }
  }

  function getSendMessage(address _addr, uint _index) public constant returns (string, uint, string) {
    //require(_index <= sendMessages[_addr].length);
    uint sendIndex = sendMessages[_addr].length - _index;
    uint messageId = sendMessages[_addr][sendIndex];
    return (cheerMessages[messageId].screenName, cheerMessages[messageId].timestamp, cheerMessages[messageId].message);
  }

  function getRecieveMessage(address _addr, uint _index) public constant returns (string, uint, string) {
    //require(_index <= recieveMessages[_addr].length);
    uint recieveIndex = recieveMessages[_addr].length - _index;
    uint messageId = recieveMessages[_addr][recieveIndex];
    return (users[cheerMessages[messageId].from], cheerMessages[messageId].timestamp, cheerMessages[messageId].message);
  }

  function getSingleCheerMessage(bytes32 _hashedId) public constant returns (string, uint, string) {
    uint messageId = cheerHashedIdPureIdMap[_hashedId];
    return ( users[cheerMessages[messageId].from], cheerMessages[messageId].timestamp, cheerMessages[messageId].message);
  }

  function sendThanksMessage(string _thanksMessage, uint _amount, bytes32 _hashedId) public {
    address _from = msg.sender;
    transfer(_admin_address, _amount);
    uint cursor = thanksSendMessages[_from].length;
    for( uint i = cursor; i < cursor + _amount; i++) {
      address _to = cheerMessages[recieveMessages[_from][i]].from;
      thanksMessages.push(ThanksMessage(_from, _to ,_thanksMessage, _hashedId, now));
      thanksHashedIdPureIdMap[_hashedId] = thanksMessages.length - 1;
      thanksSendMessages[_from].push(thanksMessages.length-1);
      thanksRecieveMessages[_to].push(thanksMessages.length-1);
    }
  }

  function getThanksRecieveMessage(address _addr, uint _index) public constant returns (string, uint, string) {
    //require(_index <= recieveMessages[_addr].length);
    uint recieveIndex = thanksRecieveMessages[_addr].length - _index;
    uint messageId = thanksRecieveMessages[_addr][recieveIndex];
    return (users[thanksMessages[messageId].from], thanksMessages[messageId].timestamp, thanksMessages[messageId].message);
  }
  function getSingleThanksMessage(bytes32 _hashedId) public constant returns (string, uint, string) {
    uint messageId = thanksHashedIdPureIdMap[_hashedId];
    return ( users[thanksMessages[messageId].from], thanksMessages[messageId].timestamp, thanksMessages[messageId].message );
  }

  function adduser(string _screenName) public {
    if (isSameString(users[msg.sender],"")) {
      users[msg.sender] = _screenName;
      userMap[s2b16(_screenName)] = msg.sender;
      uint num_of_temp_mes = tempMessages[s2b16(_screenName)].length;
      for( uint i = 0; i < num_of_temp_mes; i++ ) {
        cheerMessages[tempMessages[s2b16(_screenName)][i]].to = msg.sender;
        recieveMessages[msg.sender].push(tempMessages[s2b16(_screenName)][i]);
      }
      // will fix : transfer from contract to user
      //_transfer(_admin_address,msg.sender,num_of_temp_mes);
      _mint(msg.sender,num_of_temp_mes);
    }
  }

  function getuser() public constant returns (string) {
    return users[msg.sender];
  }

  function finduser(string _screenName) public constant returns (address ) {
    return userMap[s2b16(_screenName)];
  }

  function s2b16(string s) internal returns (bytes16) {
    bytes memory b = bytes(s);
    uint r = 0;
    for (uint i = 0; i < 16; i++) {
      if (i < b.length) {
        r = r | uint(b[i]);
      }
      if (i < 15) r = r * 256;
    }
    return bytes16(r);
  }

  function isSameString(string _origin, string _target) public pure returns (bool) {
    return keccak256(abi.encodePacked(_origin)) == keccak256(abi.encodePacked(_target));
  }

  constructor (address admin_address) ERC20Detailed(_name, _symbol, _decimals) public {
    _mint(admin_address, 1000000000); // 100,000,000CHEER
    users[admin_address] = "Cheer master";
    _admin_address = admin_address;
  }

  modifier isReadyForCheer(string _screenName) {
    address _from = msg.sender;
    uint[] memory myMessageIdxs = sendMessages[_from];
    uint myLastSendMessageTime;

    if( myMessageIdxs.length <= 0 ) {
      require(true);
      _;
    }
    else {
      myLastSendMessageTime = getMyLastSendMessageTime(_screenName, myMessageIdxs);
      require( (myLastSendMessageTime + cheerCoolDownTime <= now ), "Cheers need to be open at 24 hours.");
      _;
    }
  }

  function getMyLastSendMessageTime(string _screenName, uint[] _myMessageIdxs) private view returns (uint) {
    for (uint i = _myMessageIdxs.length -1; i >= 0 ; i--) {
        if( keccak256(cheerMessages[_myMessageIdxs[i]].screenName) == keccak256(_screenName) ) {
          return cheerMessages[_myMessageIdxs[i]].timestamp;
        }
        else {
          return now - 1 days;
        }
    }
  }


}
